import pickle
corpus = pickle.load(open("/content/drive/My Drive/Colab Notebooks/workspace_henceforth_ensmr/workspace_henceforth/corpus.pkl", "rb"))

"""**Building LDA and LSI models**"""

from gensim import models, corpora
dictionary = corpora.Dictionary(corpus)
corps = [dictionary.doc2bow(text) for text in corpus]
print(corps[20])

# Build the LDA model
lda_model = models.LdaModel(corpus=corps, num_topics=20, id2word=dictionary)
 
# Build the LSI model
lsi_model = models.LsiModel(corpus=corps, num_topics=20, id2word=dictionary)

lda_model.save('lda_model.model')
lsi_model.save('lsi_model.model')

"""**Let’s now display the topics the two models have inferred:**"""

print("LDA Model:")
 
for idx in range(20):
    # Print the first 10 most representative topics
    print("Topic #%s:" % idx, lda_model.print_topic(idx, 10))
 
print("=" * 20)
 
print("LSI Model:")
 
for idx in range(20):
    # Print the first 10 most representative topics
    print("Topic #%s:" % idx, lsi_model.print_topic(idx, 10))
 
print("=" * 20)

"""**Model** **Evaluation**"""

from gensim.models.coherencemodel import CoherenceModel
cm_lda = CoherenceModel(model=lda_model, corpus=corps, coherence='u_mass')
cm_lsi = CoherenceModel(model=lsi_model, corpus=corps, coherence='u_mass')
coherence_lda = cm_lda.get_coherence()
coherence_lsi = cm_lsi.get_coherence()
print("coherance_lda= %s" % coherence_lda)
print("coherance_lsi= %s" % coherence_lsi)

